mapper.select:
select * 
from trade 
where type_div = 'au';

select * 
from trade 
where type_div = 'ran';

select * from notice
where noti_title LIKE concat('%', #{keyword}, '%')
order by noti_num desc limit #{displayPost}, #{postNum}

select * from notice
where noti_title LIKE concat('%', #{keyword}, '%') or noti_content LIKE concat('%', #{keyword}, '%')
order by noti_num desc limit #{displayPost}, #{postNum}

mapper.update:
UPDATE notice
noti_title = #{noti_title}, noti_content = #{noti_content}, noti_image = #{noti_image}, noti_image1 = #{noti_image1}, noti_image2 = #{noti_image2},
WHERE noti_num = #{noti_num}
