mapper.select:
select room_id from chat_room
where product_num = #{product_num}
and buyer = #{buyer}

SELECT cr.room_id, cr.product_num, p.product_seller, p.product_title, m.member_nickname
FROM chat_room cr
JOIN product p ON cr.product_num = p.product_num
JOIN member m ON p.product_seller = m.member_id
WHERE cr.seller = #{id} OR cr.buyer = #{id}
GROUP BY cr.room_id, cr.product_num, p.product_seller, p.product_title, m.member_nickname;

  	select * from chat_message
	where room_id = #{room_id}
    order by chat_date
