mapper.select:
select p.product_pic,p.product_title,p.product_price,p.product_date,p.product_status,p.product_num,p.product_seller
from product p
where product_seller = #{id}

mapper.update:

update member
set member_pass = #{member_pass}, member_phone = #{member_phone}, member_nickname = #{member_nickname}, member_email = #{member_email},
member_address = #{member_address},member_address2 =#{member_address2},member_address3 =#{member_address3}, member_pic = #{member_pic},member_pay = #{member_pay}
where member_id = #{member_id} and member_num = #{member_num}

UPDATE member
SET member_pic = #{member_pic}
WHERE member_id = #{id}
